function annotation = register_annotation_centroid(...
    source_annotation, ...
    target_volume, ...
    frame_size)

[v_loc, a_loc] = get_annotation_volume(...
    source_annotation, target_volume, 'size', frame_size);
position_offset = centroid(v_loc) - a_loc.position();
annotation = source_annotation.clone_child();
annotation.position = source_annotation.position + position_offset;